function executeResponse(json) {
	$.each(json,
		function(i, command) {
            if (command != null) {
                switch (command.type) {
                    case 'SCRIPT' :
                        eval(command.script);
                        break;
                    case 'HTML' :
                        var aim = $("#"+command.id);
                        if (aim) {
                            switch (command.mode) {
                                case 'PREPEND' :
                                    aim.html(command.html + aim.html());
                                    break;
                                case 'REPLACE' :
                                    aim.html(command.html);
                                    break;
                                case 'APPEND' :
                                    aim.append(command.html);
                                    break;
                                case 'OUTER_REPLACE' :
                                    aim.replaceWith(command.html);

                                    // ajax trigger wieder an change event  binden
                                    $("#"+command.id).find('select.ajaxPostFormOnChange').change(function() {
                                        ajaxBinder($(this).attr('id'));
                                    });

                                    // ajax trigger wieder an blur event binden
                                    if($("#"+command.id).find('.ajaxPostFormOnBlur').length > 0) {
                                        $("#"+command.id).find('.ajaxPostFormOnBlur').blur(function() {
                                            id = $(this).attr('id');
                                            window.setTimeout("ajaxBinder('" + id + "')", 100);
                                        });
                                    }

                                    // Die infobuttons wieder binden
                                    if($("#"+command.id).find('.infobutton').length > 0) {
                                        try {
                                            $("#"+command.id).find('.infobutton').infobutton();
                                        }
                                        catch(e) {}
                                    }

                                    // autocompleter neu erzeugen?
                                    $('#'+command.id+' .autocomplete:text').each(
                                        function (i) {
                                            $(this).unbind();
                                            eval('createAutocompleterFor'+this.id+'()');
                                        }
                                    );

                                    // Mouseover Fehlertexte wieder erzeugen (ergebnisorientierte Ratgeber)
                                    if($('#eingabe').length > 0 && $.fn.errorbutton) {
                                        $('#eingabe li.invalid').errorbutton();
                                    }

                                    break;
                                default :
                                    break;
                            }
                        }
                        break;
                    default :
                        break;
                }
            }
		}

	);
	if(showSelectUnitInput) {
		showSelectUnitInput();
	}
	// Sortiert die Tabindex neu nach Ajaxaufruf #DH030210
	tabssort();

	// Funktion nur in ESK vorhanden
	// @see js/esk/forms.js
	if($.isFunction(window.initEskShowHide)) {
		// Initisialisierung des EskShowHide-Widgets
		initEskShowHide();
	}

	// Funktion nur in ESK vorhanden
	// @see js/esk/base.js
	if($.isFunction(window.initImportexportButtons)) {
		// Initialisierung von Buttons im Formular fuer Im-/Export
		initImportexportButtons();
	}

	// Wir triggern noch ein Event am body, in das sich andere scripte rein haengen koennen
	$('body').trigger('rgfwPostAjax').trigger('initJsConstraints').removeClass('rgfwAjaxLoading');

}

var disabledFields = [];
/**
 * Updatet das Formular mit einem Ajaxrequest.
 *
 * @param string url Zielurl
 * @param object formElement Die Form die das Ajay ausgeloest hat.
 * @param strinng elementid Id des Elements das geaendert wurde.
 */
function updateFormular(url, formElement, elementId){
	if (!waitingForResponse) {
		waitingForResponse = true;
		// Wir triggern noch ein Event am body, in das sich andere scripte rein haengen koennen
		$('body').trigger('rgfwPreAjax').addClass('rgfwAjaxLoading');
		$.post(url, formElement.serialize(), function(data, textStatus) {
			if(JSON && JSON.parse) {
				data=JSON.parse(data);
			}
			else {
				data = jQuery.parseJSON(data);
			}

			if (disableFieldsOnAjaxRequest) {

				formElement.find('input, select, button').each(function() {
					var enableField = true;
					for (var i in disabledFields) {
					   if (disabledFields[i] == getTagOrTagSerieElementName($(this))) {
						   enableField = false;
						   break;
					   }
					}
					if (enableField == true) {
						$(this).removeAttr("disabled");
					}
					else {
						// zur Sicherheit das Attribut noch mal setzen, da es bei ausgetauschten
						// Ajaxfeldern sonst nixcht da waere
						$(this).attr("disabled", true);
					}

				});
			}

			executeResponse(data);

			if (data.ergebnisorientiert != null) {
				//updateErgebnis(data.ergebnisorientiert);
				$("body").trigger("updateErgebnis", [data.ergebnisorientiert]);
			}

			if (data.validationresult != null) {
				//updateErgebnis(data.validationresult);
				$("body").trigger("updateErgebnis", [data.validationresult]);
			}

			// wir horchen auf fokus wechsel und merken uns das aktuell fokussierte element per id
			$('input[type="text"], input[type="checkbox"], select').bind('focus', saveFocusedElementId);

			waitingForResponse = false;
			refocus();
			formElement.trigger('ajaxCompleted');
		}, 'text');

		// nur wenn die globale variable disableFieldsOnAjaxRequest true ist sollen die felder auch disabled werden
		if (disableFieldsOnAjaxRequest) {
	        disabledFields = [];

			formElement.find('input, select, button').each(function() {
				if($(this).attr("disabled") == "disabled" || $(this).attr("disabled") == true) {
					disabledFields.push(getTagOrTagSerieElementName($(this)));
				}
				$(this).attr("disabled", "disabled");
			});
		}
	}
}

/**
 * Ermittelt den Tagnamen innerhalb des Formulars.
 * Bei eiem Tag wie z. B.
 * 		<input id="typ0" type="text" value="import" name="formularfeld_text1">
 * wird "formularfeld_text1" geliefert
 *
 * Bei SerienTags wie RadioButtons, Checkboxen oder Selectboxen wid der Value mit hernagezogen.
 * Bei eiem Tag wie z. B.
 * 		<input id="typ0" type="text" value="import" name="radio_serie_1">
 * wird "radio_serie_1.typ0" geliefert.
 *
 * @param JQueryObject jqElement Das JQ-element aus dem der Name ermittelt werden soll.
 */
function getTagOrTagSerieElementName(jqElement){

	var name = jqElement.attr('name');

	if(
		jqElement.prop("tagName").toLowerCase() === 'input'
		&&
		jqElement.attr('type').toLowerCase() !== undefined
		&&
		$.inArray( jqElement.attr("type").toLowerCase(), new Array('radio','checkbox','option')) >= 0
	) {
		name += '.' + jqElement.attr('id');
	}

	return name;
}

/**
 * Hilfsfunktion um die Ajaxfunktionalitaet an einzelne Objekte zu binden.
 *
 * @param object id des Elements an das die Funktion gebunden werden soll.
 */
function delayedAjaxBinder(elId, delay) {
	el = $('#'+elId);
	var ajaxForm = el.parents('form').eq(0);
	updateFormular(ajaxForm.attr('action') + "&isAjax=true&selectKey=" + el.attr('id'), ajaxForm, el.attr('id'));
}

/**
 * Hilfsfunktion um die Ajaxfunktionalitaet an einzelne Objekte zu binden.
 *
 * @param string elId Die ID des Elements
 * @param string attribute optional Der Name des Attributs fuer den Selectkey
 */
function ajaxBinder(elId, attribute, additionalParameter) {
	if((typeof elId) == 'object') {
		el = elId;
	}
	else {
		el = $('#'+elId);
	}
	var ajaxForm = el.parents('form').eq(0);

	var testTargetUrl = ajaxForm.find('.formStepHelperAjaxTarget');
	if(testTargetUrl.length > 0) {
		var targetUrl = testTargetUrl.eq(0).val();
	}
	else {
		var targetUrl = ajaxForm.attr('action')
	}
	if (ajaxBinder.arguments.length > 1) {
		attribute = ajaxBinder.arguments[1];
	}
	else {
		attribute = 'id';
	}

    var selectkeyData = el.attr(attribute).match(/^(.*)\[\]$/);
    var selectkey = selectkeyData ? selectkeyData[1] : el.attr(attribute);

    var ergebnisorientiertparam = '';

    if (typeof(ergebnisorientiert) != 'undefined' && ergebnisorientiert == true) {
        ergebnisorientiertparam = "&ergebnisorientiert=true";
        $('h3.ui-state-active').addClass('loading');
    }

    if(typeof(additionalParameter) == 'undefined') {
    	additionalParameter = '';
    }

    updateFormular(targetUrl + "&isAjax=true&selectKey=" + selectkey + ergebnisorientiertparam + additionalParameter, ajaxForm, el.attr('id'));
}

