/**
 * Bindet Klick-Event an das Objekt, bei dem die Lightbox geoeffnet wird
 * @param object
 */
function addLightboxTrigger(object) {
	// Bei Klick Lightbox oeffnen
	$(object).click(function (event) {

		// Standardverhalten fuer Klicks unterbinden
		event.preventDefault();

		// Id der zu oeffnenden Lightbox
		var lightboxId = $(object).attr('data-lightbox-id');

		// Lightbox-Element
		var lightbox = $('#' + lightboxId);

		// Ajax-Url
		var ajaxUrl = $(object).attr('href');

		// Lightbox oeffnen
		lightbox.foundation('reveal', 'open', function () {

			// Lightbox relativ zum body positionieren
			moveLightboxToEndOfBody(lightbox);

			logCalledURL(ajaxUrl);

			$("body").trigger("initJsConstraints");

			// Close-Event
			lightbox.bind('closed', function () {
				// Lightbox-Inhalt beim Schliessen wieder durch Warte-Raedchen ersetzen
				$('#' + lightboxId).find('.lightbox-frame').html('<div class="waiting"></div>');
				$("body").trigger("lightboxIsClosed");
			});


			///////////////////////////
			// Lightbox-Inhalt laden //
			///////////////////////////

			// Inhalt ueber Ajax laden:
			$.getJSON(ajaxUrl, function (data) {

				// Seite konnte geladen werden
				if (data.success == 1) {

					// Seite mit den Formular-Containern anzeigen
					lightbox.find('.lightbox-frame').replaceWith(data.html);

					// Formular-Elemente neu binden
					rebindFormElements(lightbox.find('.lightbox-frame, .form-container').find('form.mainform'));

				}

			});

		});

	});
}

/**
 * Initialisiert Ajax-Buttons in Formularen fuer die Formularverarbeitung
 *
 * @author Tassilo Margraf
 * @param object form            <form>-Object
 */
function initAjaxButtons(form) {

	// Alle Ajax-Buttons durchlaufen (nicht die Buttons initialisieren, die Lightboxen triggern)
	form.find('.ajaxButton').not('.noAjaxTrigger').each(function () {

		// Klick-Event
		$(this).click(function (event) {

			// Standardverhalten fuer Klicks unterbinden
			event.preventDefault();

			// Button auf 'disable' setzen, um ein mehrfaches Absenden des Formulars zu vermeiden
			$(this).attr('disabled', 'disabled');

			// Id des Formular-Containers
			var formId = form.attr('id');

			// Ajax-Url auf Formular-Action
			var url = form.attr('action') + '&formaction=' + $(this).attr('id');

			// Formular via POST und Ajax verarbeiten
			$.post(url, form.serialize(), function (data, textStatus) {

				// Felder auf disable setzen
				disableFields(form);

				// Seite konnte geladen werden
				if (data.success == 1) {

					// Felder wieder auf enabled setzen
					enableFields(form);

					// Alte Hinweis-Texte ueber dem Formular entfernen, wenn vorhanden
					form.find('.hint').each(function () {
						$(this).remove();
					});

					// Container in den das Ergebnis eingefuegt werden soll
					var insertContainer = $('#' + data.insert_container_id);

					// Neuen Formular-Inhalt in Formular-Container einfuegen
					insertContainer.html(data.html).promise().done(function () {

						// Formular nach dem Einfuegen des Inhalts
						var formAfterInsert = insertContainer.find('form.mainform');

						// Formular-Elemente neu binden
						rebindFormElements(formAfterInsert);

						// Eventuell offene Lightbox nach Scrolling wieder neu positionieren
						var lightbox = $('.reveal-modal.open');
						if (lightbox.length == 1) {
							lightbox.css('top', $(window).scrollTop() + 50);
						}


						// An dieser Stelle koennen spezielle Aktionen nach dem Verarbeiten
						// eines Formulars ausgefuehrt werden
						switch (formAfterInsert.attr('id')) {

							// Formular Haushaltsdaten
							case 'manageHouseholdData':

								// Ggf. Formular fuer die erweiterten Haushaltsdaten einblenden
								if (data.showFormHouseholdDataExtended == 1) {
									// Einblenden ...
									$('#householdDataExtended').removeClass('hide');
									// ... und neu laden
									var url = $('#manageHouseholdDataExtendedContainer').attr('data-form-url');
									loadForm('manageHouseholdDataExtendedContainer', url, true);
								}
								else {
									// Ausblenden
									$('#householdDataExtended').addClass('hide');
								}

								break;


							// Formular Nutzerdaten: Formular fuer die Adresse neu laden
							case 'formUserData':

								// URL aus Data-Attribut
								var url = $('#manageAddressContainer').attr('data-form-url');

								// Formular laden
								loadForm('manageAddressContainer', url, true);

								break;

							// Formular zum Event eintragen
							case 'formManageEvents':
								if (lightbox) {
									// Das Formular ist in einer Lightbox offen, bei Erfolg kann diese geschlossen werden
									if (formAfterInsert.find(".error, .notice").length == 0) {
										// Hinweistexte entfernen
										lightbox.foundation('reveal', 'close');
									}
								}
								break;

						}

					});

				}

			});


		});

	});

}


/**
 * Laedt ein ueber Ajax verarbeitetes Formular und fuegt es an einer
 * durch eine ID gekennzeichnete Stelle im DOM ein.
 *
 * @param string formContainer    id des Formular-Containers in welchen das Formular nach dem Laden
 *                                eingefuegt werden soll
 * @param string url            URL zum Laden des Formulars
 * @param boolean showWaiting    Waiting-Animation einfuegen?
 */
function loadForm(formContainerId, url, showWaiting) {

	// Formular-Container
	var formContainer = $('#' + formContainerId);

	// Warten ... : wird durch das Laden des Inhalts wieder entfernt
	if (showWaiting == true) {
		formContainer.html('<div class="waiting"></div>');
	}

	// Url fuer die Anzeige des Formulars laden
	$.getJSON(url, function (data) {

		// Seite konnte geladen werden
		if (data.success == 1) {

			// Inhalt einfuegen
			formContainer.html(data.html);

			// Events auf Formularelemente neu binden, z. B. Infobuttons
			rebindFormElements($('#' + formContainerId).find('form.mainform'));

		}

	});

	// Standard-Ladeverhalten unterbinden
	return false;


} // loadForm()


/**
 * Elemente in einem Formular wieder binden, nachdem der Inhalt ueber Ajax geladen wurde
 *
 * @see executeResponse()    Analog zu ...
 * @param object form        <form>-Object
 */
function rebindFormElements(form) {

	// Ajax ausloesende Elemente wieder binden
	// @see berater182.js
	initRgfwAjaxTrigger();

	// Ajax ausloesende Buttons wieder binden
	initAjaxButtons(form);

	// Ajax-Trigger wieder an blur event binden
	if (form.find('.ajaxPostFormOnBlur').length > 0) {
		form.find('.ajaxPostFormOnBlur').blur(function () {
			id = $(this).attr('id');
			window.setTimeout("ajaxBinder('" + id + "')", 100);
		});
	}

	// Infobuttons
	if (form.find('.infobutton').length > 0) {
		try {
			form.find('.infobutton').infobutton();
		}
		catch (e) {
		}
	}

	// Tabindex neu sortieren
	var tabindex = 1;
	form.find('input,select').each(function () {
		if (this.type != "hidden") {
			$(this).attr("tabindex", tabindex);
			tabindex++;
		}
	});

	// Widget zum aendern von Email und Passwort nur bei Update des Formular Nutzerdaten initialisieren
	if (form.attr('id') == 'formUserData') {
		initEskLabelOutputChange();
	}

	// PV-Anlage: Widget zur Eingabe der Dachausrichtung und Dachneigung
	if (form.find('#formrowausrichtung').length > 0) {
		initDachAusrichtungWidgets();
	}

	$("body").trigger("rebindFormElementsFinished").trigger('initJsConstraints');

} // rebindFormElements()


