/**
 * Saves, wether the Cookie-Question was already asked.
 * @type {Boolean}
 */
var IsCookieSet = false;
var CookieParameterName = "CookieConfirmed";
var CookieParameterValue = "true";
$(document).ready(function() {
    /**
     * Hides the Cookie-Question und sets the Expires-Time
     */
    function hideCookieHint() {
        $("#cookieHint").addClass("hidden").removeClass("shown");
        IsCookieSet = true;
        $.cookie(
            CookieParameterName,
            CookieParameterValue,
            {
                path: '/',
                expires: 180
            }
        );
    }

    $("#cookieHint button").bind("click", hideCookieHint);
    // the cookie-question wasn't asked yet
    if ($.cookie(CookieParameterName) == "true") {
        IsCookieSet = true;
    }
    else {
        $("#cookieHint button").bind("click", hideCookieHint);
        $("#cookieHint").removeClass("hidden").addClass("shown");
    }
});
