var diagramNavigationId = "#navigation-form";
/**
 * Erstellt ein neues Diagramm anhand von den aktuellen Formular-Inhalten.
 * @param {Boolean} init
 */
function getNewDiagrammHtml(init) {
	if (init === true) {

		// region +WartenOverlay nur fuer das Diagramm - dynamisch anlegen, da es an verschiedenen Stellen eingebunden wird - und auch gleich aktivieren.

		// Parameter setzen.
		var waitingPaneClassName = '.waiting';
		var waitingPaneAreaSelector = '.diagramInner .diagram';

		// WartenOverlay anlegen (ist dann auch gleich aktiv), wenn kein WartenOvelray im zustaendigen DivContainer des Diagramms vorhanden ist.
		if ($(waitingPaneAreaSelector + ' div' + waitingPaneClassName).length === 0) {
			$(waitingPaneAreaSelector).append('<div class="waiting"></div>');
		}
		// WartenOverlay aktivieren, wenn bereits ein WartenOvelray im zustaendigen DivContainer des Diagramms vorhanden ist.
		else {
			$(waitingPaneAreaSelector).find(waitingPaneClassName).show();
		}
		// endregion

		// Formular per Ajax versenden
		$.ajax({
			type: "GET",
			url: $(diagramNavigationId).attr("action"),
			data: $(diagramNavigationId).serialize() + "&navigation=0",
			success: function (data) {
				// Diagramm ersetzen.
				$(".diagramInner .diagram").html(data.html);
				// WartenOverlay wieder entfernen - genauer: es deaktivieren.
				$(waitingPaneAreaSelector).find(waitingPaneClassName).hide();
			}
		});
	}
};
