		//endregion

		//region +Function: Liefert eine Zahl aus einer mit einer Einheit belegten Nummer/String.
		/**
		 * Liefert eine Zahl. Bereinigt einen Massangabenwert, wie z. B. "186px" und liefert 186 (als Zahl).
		 *
		 * cssPropertyText Massangabenwert aus z. B. css wie z. B. "186px"
		 * return Zahl wie z. B. 186
		 */
		$.getNumberCleanedFromUnit = function (cssPropertyText) {
			if (cssPropertyText !== undefined && cssPropertyText !== null) {
				return 1 * cssPropertyText.replace(/\D+$/g, '');
			}
		}
		//endregion

		//region +Function: Reaktion auf gedrueckte Taste.
