		//region +Funktion: $.feedback_push_screenshot_erstellen_und_overlay_oeffnen2 ... ist zweiter Teil der Fuktion $.feedback_push_screenshot_erstellen_und_overlay_oeffnen()
		/*
		 * Erstellt zum aktuellen Zeitpunkt einen Scrrenshot und oeffnet nach dieser Momentaufnahme das PushFeedbackOverlay zur Bearbeitung.
		 * ###screenshotErstellen###getScreenshot###makeScreenshot###
		 */
		$.feedback_push_screenshot_erstellen_und_overlay_oeffnen2 = function () {

			html2canvas(
				// Element definieren, dessen Inhalt als Shortcut erstellt werden soll.
				[
					// Nur den relevante Auswschnitt als Screenshot auslesen (auch den im Fenster nicht sichtbaren). ABER: offenbar MUSS eine ID benutzt werden.
					$.domObjectToScreenshot
				],
				{
					// Wenn der Screenshot erstellt wurde, dann in das Anzeigelement einspeisen und das PushFeedbackOverlay zum Bearbeiten oeffnen.
					onrendered: function (canvas) {

						// Der Screenshot ist erstellt, jetzt all Grafiken durch die echten SVG's wieder zurueckersetzen.
						$.rueckgaengigErsetzenUndErgenazenNachDemScreenshot();

						var imageScaliererContainer = $('#feedback_push_screenshot_vorschaubild_scalierer');

						// werfe alle alten canvases raus
						imageScaliererContainer.empty();

						// Ein CanvasObjekt in seinem Skalierungcotainer anlegen.
						$(canvas).attr('id', 'screenshotOriginCanvas_push_feedback');
						// ! wichtig sonst gibt sirgednwelche blöden verscheiebungen, hoeh eund breite gleich haltne in attribunten ud css
						imageScaliererContainer.append(canvas);

						//region +###ScreenshotScalieren###

						$.screenshotVerticalSliderWidth = 20;

						var screenshotWidth = $(canvas).attr('width');
						var screenshotContainerWidth = $('#feedback_push_screenshot_vorschaubild').css('width');
						screenshotContainerWidth = $.getNumberCleanedFromUnit(screenshotContainerWidth);
						$.scalFaktor = (screenshotContainerWidth - $.screenshotVerticalSliderWidth) / screenshotWidth;

						$.scalFactorX = $.scalFaktor;
						$.scalFactorY = $.scalFaktor;

						var a = $(canvas);

						var h = a.attr('width');
						var hnew = $.scalFactorX * h;
						a.css('width', hnew);

						var h = a.attr('height');
						var hnew = $.scalFactorY * h;
						a.css('height', hnew);

						//endregion
					}
				}
			);
		};
