				var a = $('#screenshotOriginCanvas_push_feedback');
				var originBreiteVorScalierung = a.width();
				var originBreiteNachScalierung = originBreiteVorScalierung * $.scalFaktor;
				var originBreiteBorderLeft = (originBreiteVorScalierung - originBreiteNachScalierung) / 2;
				$.originPositionshiftLeft = -originBreiteBorderLeft;
